    [Table("TPACKING")]
    public class PackingModel
    {
        /// <summary>
        /// <para>TPACKING.PACKKEYID</para>
        /// The internal database KeyID.
        /// </summary>
        [Column("PACKKEYID")]
        [Key]
        [Required]
        public int KeyId { get; set; }

        /// <summary>
