        /// </summary>
        [Column("TEXT_MODIFIED", TypeName = "datetime")]
        public DateTime? Modified { get; set; }

        /// <summary>
        /// <para>TTEXT.TEXT_MODIFIEDKEYID</para>
        /// </summary>
        [Column("TEXT_MODIFIEDKEYID")]
        public int? ModifiedKeyId { get; set; }

